import axios from "axios"
import React, { useEffect, useState } from 'react'

const Records = () => {
    const [student, setStudent] = useState({
        id: "", firstname: "", lastname: "", phonenumber: "", zip: "", statecode: "", statename: ""
    })

    const [data, setData] = useState(null);
    const state = { button: 1 };

    const onChangeId = (e) => {
        setStudent({ ...student, [e.target.name]: e.target.value })
    }
    const onChangeFirstName = (e) => {
        setStudent({ ...student, [e.target.name]: e.target.value })
    }
    const onChangeLastName = (e) => {
        setStudent({ ...student, [e.target.name]: e.target.value })
    }
    const onChangePhoneNumber = (e) => {
        setStudent({ ...student, [e.target.name]: e.target.value })
    }
    const onChangeZip = (e) => {
        setStudent({ ...student, [e.target.name]: e.target.value })
    }
    const onChangeStateCode = (e) => {
        setStudent({ ...student, [e.target.name]: e.target.value })
    }
    const onChangeStateName = (e) => {
        setStudent({ ...student, [e.target.name]: e.target.value })
    }

    async function StudentData(e) {
        e.preventDefault();
        if (state.button === 1) {
            axios
                .post("http://localhost:3300/students/", student)
                .then(() => alert(`successful button  1 ${student.firstname}`))
                .catch((err) => {
                    console.log(err);
                    alert("Error detected");
                });
            setStudent({ id: "", firstname: "", lastname: "", phonenumber: "", zip: "", statecode: "", statename: "" })
        }
        // if(state.button ===2)
    }
    useEffect(() => {
        axios
            .get("http://localhost:3300/students")
            .then((response) => {
                console.log("response::", response.data)
                setData(response.data)
            })
            .catch((err) => {
                alert(`Promo code has already been used`)
            })
    }, []);

    return (
        <form onSubmit={StudentData}>
            <div>
                <label>Id:</label>
                <input
                    type="number" name="id" value={student.id} onChange={onChangeId}></input>
                <br />
                <label>firstname:</label>
                <input
                    type="text" name="firstname" value={student.firstname} onChange={onChangeFirstName}></input>
                <br />
                <label>lastname:</label>
                <input
                    type="text" name="lastname" value={student.lastname} onChange={onChangeLastName}></input>
                <br />
                <label>phonenumber:</label>
                <input
                    type="text" name="phonenumber" value={student.phonenumber} onChange={onChangePhoneNumber}></input>
                <br />
                <label>zip:</label>
                <input
                    type="text" name="zip" value={student.zip} onChange={onChangeZip}></input>
                <br />
                <label>statecode:</label>
                <input
                    type="number" name="statecode" value={student.statecode} onChange={onChangeStateCode}></input>
                <br />
                <label>statename:</label>
                <input
                    type="text" name="statename" value={student.statename} onChange={onChangeStateName}></input>
                <br />
                <button
                    type="submit" onClick={() => { state.button = 1 }}>Add
                </button>
            </div>
        </form>
    );
};

export default Records
