    }
    const onChangeFirstName = (e) => {
        setStudent({ ...student, [e.target.name]: e.target.value })
    }
    const onChangeLastName = (e) => {
        setStudent({ ...student, [e.target.name]: e.target.value })
    }
    const onChangePhoneNumber = (e) => {
        setStudent({ ...student, [e.target.name]: e.target.value })
    }
    const onChangeZip = (e) => {
        setStudent({ ...student, [e.target.name]: e.target.value })
