import React, { useEffect, useState } from 'react'
import "./SearchBar.css";
import SearchIcon from '@mui/icons-material/Search';
import axios from 'axios';
import { Typography } from '@mui/material';

function SearchStudent({ placeholder, data }) {
    const [searchStudent, setSearchStudent] = useState("");
    const [studentName, setStudentName] = useState([]);
    const onChange = (e) => {
        e.preventDefault()
        setStudentName(e.target.value)
    }

    useEffect(() => {
        axios
            .get("http://localhost:3300/students")
            .then((response) => {
                setStudentName(response.data);
            })
            .catch((err) => {
                console.log(err);
            })
    }, [searchStudent])
    return (
        <div className='search'>
            <Typography variant="h3">
                Student List
            </Typography>

            <div className='searchInputs'>
                <input type="text" placeholder={placeholder} value={searchStudent} onChange={onChange} />
                <div className='searchIcon'>
                    <SearchIcon />
                </div>
            </div>
            <div className='dataResult'>
                <table>
                    <tbody>
                        <tr>
                            <th>Id</th>
                            <th>FirstName</th>
                            <th>LastName</th>
                            <th>PhoneNumber</th>
                            <th>Zip</th>
                            <th>StateCode</th>
                            <th>StateName</th>
                        </tr>
                        {studentName?.filter((val, id) => {
                            if (val.firstname || val.lastname === "Brandon") {
                            } return (
                                <tr>
                                    <td>{val.id}</td>
                                    <td>{val.firstname}</td>
                                    <td>{val.lastname}</td>
                                    <td>{val.phonenumber}</td>
                                    <td>{val.zip}</td>
                                    <td>{val.statecode}</td>
                                    <td>{val.statename}</td>
                                </tr>
                            )
                        })}
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default SearchStudent
