import Records from "./components/Records"
import SearchStudent from "./components/SearchStudents";
import React, { useEffect, useState } from 'react'
import axios from 'axios'
import "./App.css"

function App() {
  const [studentName, setStudentName] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:3300/students')
      .then((response) => {
        setStudentName(response?.data)
      }).catch(err => {
        console.log(err);
        alert('An error occurred within the rsponse.')
      })
  }, [])
  return (
    <div className="App">
      <SearchStudent placeholder={"Enter student name."} data={studentName}/>
      <Records />
    </div>
  );
}

export default App;
