import axios from "axios"
import React, { useEffect, useState } from 'react'

const Records = () => {
    const [student, setStudent] = useState({
        id: null, firstname: null, lastname: null, phonenumber: null, zip: null, statecode: null, statename: null
    })
    const state = { button: 1 };

    // useEffect(() => {
    //     axios
    //         .post("http://localhost:3300/students", student)
    //         .then(() => alert(`Student ${student.firstname} ${student.lastname} added successful.`))
    //         .catch((err) => {
    //             alert("Error detected");
    //         });
    //     setStudent({ ...student })
    // })

    return (
        // <form onSubmit={/**/}>
        <div>
            <label>Id:</label>
            <input
                type="number" name="id" value={student.id} onChange={onChangeId}></input>
            <br />
            <label>firstname:</label>
            <input
                type="text" name="firstname" value={student.firstname} onChange={onChangeFirstName}></input>
            <br />
            <label>lastname:</label>
            <input
                type="text" name="lastname" value={student.lastname} onChange={onChangeLastName}></input>
            <br />
            <label>phonenumber:</label>
            <input
                type="text" name="phonenumber" value={student.phonenumber} onChange={onChangePhoneNumber}></input>
            <br />
            <label>zip:</label>
            <input
                type="text" name="zip" value={student.zip}></input>
            <br />
            <label>number:</label>
            <input
                type="number" name="statecode" value={student.statecode}></input>
            <br />
            <label>statename:</label>
            <input
                type="text" name="statename" value={student.statename}></input>
            <br />
            <button
                type="submit" onClick={() => { state.button = 1 }}>
            </button>
        </div>
        // </form>
    );
};

export default Records
